#!/bin/bash
# Scan for .env files and analyze their contents
# Usage: ./scripts/setup/scan-env-files.sh [directory]

set -euo pipefail

# Colors
GREEN='\033[0;32m'
BLUE='\033[0;34m'
YELLOW='\033[1;33m'
NC='\033[0m'

SEARCH_DIR="${1:-/Users/joebowers/Library/CloudStorage/Dropbox/Caveman Creative/THE WELL_Digital Assets/The Well Code}"

echo ""
echo -e "${BLUE}Scanning for .env files in:${NC}"
echo "$SEARCH_DIR"
echo ""

# Find all .env files
env_files=$(find "$SEARCH_DIR" -name ".env" -type f 2>/dev/null | \
    grep -v node_modules | \
    grep -v ".git" | \
    grep -v backup | \
    grep -v ".env.example" | \
    grep -v ".env.template")

if [ -z "$env_files" ]; then
    echo "No .env files found."
    exit 0
fi

echo -e "${GREEN}Found .env files:${NC}"
echo ""

# Analyze each file
